example.code:
Coordinate c;
if(Coordinate.TryParse("N 32.891 W 64.872",out c))
{
    Console.WriteLine(c); //N 32 53' 28.212" W 64 52' 20.914"
}

Coordinate c;
if(Coordinate.TryParse("N 32.891 W 64.872", new DateTime(2018,7,7), out c))
{
    Console.WriteLine(c); //N 32 53' 28.212" W 64 52' 20.914"
}

Coordinate c;
if(Coordinate.TryParse("N 32.891 W 64.872", CartesianType.Cartesian, out c))
{
    Console.WriteLine(c); //N 32 53' 28.212" W 64 52' 20.914"
}

Coordinate c;
if(Coordinate.TryParse("N 32.891 W 64.872", new DateTime(2018,7,7), CartesianType.Cartesian, out c))
{
    Console.WriteLine(c); //N 32 53' 28.212" W 64 52' 20.914"
}

CoordinatePart cp;
if(CoordinatePart.TryParse("N 32.891", out cp))
{
    Console.WriteLine(cp); //N 32 53' 28.212"
}

CoordinatePart cp;
if(CoordinatePart.TryParse("-32.891", CoordinateType.Long, out cp))
{
    Console.WriteLine(cp); //W 32 53' 27.6"
}
